import { useEffect, useState } from "react"
import styled from "styled-components";
import Image from "./Image"
import ProgressBar from 'react-animated-progress-bar';
function Movie_board(props){
    const [data,setData] =  useState(null)
    useEffect (() => {
        fetch('/movies')
        .then(res => res.json())
        .then(result => setData(result))
        .catch((err) => {console.log(err)})
    },[data])
    
    
    return(<Movie_boardcss>
    {
        !data
        ?<h1>Loading</h1>
        :data.data.map(movie => {
            return (
                <Movie>
                    <Image src= {`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
                    movieID = {movie.original_id}
                    /> 
                    <div>
                        <h3> {movie.title}</h3>
                        <div><ProgressBar width="100" trackWidth="1" percentage={movie.vote_average*10} /><p>{movie.vote_average}</p> </div>
                    </div>
                </Movie>
            )
        })
    }
    </Movie_boardcss>)

}
export default Movie_board
const Movie = styled.div`
    border-radius: 1%;
    width: 50%;
    padding-left: 30%;
    margin-bottom: 5%;
    margin-left: 0%;
    border-color: #787a7a;
    h3{
        position: relative;
        height: 1%;
        margin-top: -1%;
        margin-bottom: 7%;
        width: 100%;
        font-size: larger;
        text-shadow: 3cm;
    }
    p{
        margin: -7% 0% 5% 0%;
    }
  
`;
const Movie_boardcss = styled.div`
        display: grid;
        margin: 3% 3% 3% 3%;
        width: 100%;
        grid-template-columns: 30% 30% 30%;
    `
